mapper.select:
	 	INSERT INTO 
	 		RECRUIT
 			(SEQ
 			, NAME
 			, BIRTH
 			, FIELD3
 			, PHONE
 			, EMAIL
 			, ADDR
 			, LOCATION
 			, WORK_TYPE
 			, SUBMIT
 			)
		VALUES 
			((SELECT TO_NUMBER(NVL(MAX(SEQ),0)+1) 
			  FROM RECRUIT)
			, #{name}
			, #{birth}
			, #{field3}
			, #{phone}
			, #{email}
			, #{addr}
			, #{location}
			, #{workType}
			, #{submit})
